("use strict");

function remove_file(requirementElem, id,sl) {
        var last_record_id = $("#last_record_id_"+sl).val();
        if(last_record_id){
            var id = last_record_id;
        }else{
            var id = id;
        }
        // console.log(requirementElem);

        if(id){

            var course_id = $("#course_id").val();
            var csrf_test_name = $('[name="csrf_test_name"]').val();
            var base_urld = "<?php echo base_url(($enterprise_shortname?$enterprise_shortname:'admin'))?>";
            var status = "delete";
            $.ajax({
                url: base_url + "frontend/instructor/course_download_upload",
                type: "POST",
                data: {
                    csrf_test_name: csrf_test_name,
                    course_id: course_id,
                    file_id: id,
                    status: status,
                },
                success: function(r) {
                    toastr.success("Delete Successfully");
                    $(requirementElem).parent().parent().remove();
                },
            });
        }else{
            var RRemove=$(".downloadfile").length;
                   
            if(sl==(RRemove+1) || RRemove==1 ){
                return true;
            }else{
                $(requirementElem).parent().parent().remove();
                var k=sl;
                // alert(k);
                for(k=sl+1; k<=RRemove; k++){
                    $("#downloadable_div>#course_resoursed_"+k+"").attr({id:"course_resoursed_"+sl+""});
                    $("#course_resoursed_"+sl+">.col-md-7>#progress_"+k+"").attr({id:"progress_"+sl+""});
                    $("#course_resoursed_"+sl+">.col-md-7>#progress_"+sl+">#progress-bar_"+k+"").attr({id:"progress-bar_"+sl+""});
                    $("#course_resoursed_"+sl+">.col-md-7>#uploadStatus_"+k+"").attr({id:"uploadStatus_"+sl+""});

                    $("#downloadable_div>#course_resoursed_"+sl+">.col-auto>button.btn.btn-danger").attr({onclick:"remove_file(this,'',"+sl+")"});

                    $("#course_resoursed_"+sl+">.col-md-7>.d-md-flex.align-items-center>#downloadable_file"+k+"").attr({id:"downloadable_file"+sl+""});
                    $("#course_resoursed_"+sl+">.col-md-7>.d-md-flex.align-items-center>#downloadable_file"+sl+"").attr({onchange:"excersizefileuploadpr("+sl+",'')"});
                    sl++;  
                    // .accordion-body .row.col-auto.input-group-text
                }
            }

            
            
        }




}

