// exam question button scroll 
$(window).scroll(function(e) {
    var $el = $('.sticky');
    var isPositionFixed = ($el.css('position') == 'fixed');
    if ($(this).scrollTop() > 1000 && !isPositionFixed) {
        $el.css({
            'position': 'fixed',
            'top': '90px',
            'z-index': '1',
            // 'right': '112px'
        });
    }
    if ($(this).scrollTop() < 1000 && isPositionFixed) {
        $el.css({
            'position': 'static',
            'top': '0px'
        });
    }
});








